import React, {useEffect, useState} from 'react';
//css
import './App.css'
//components
import Searchbar from './Components/Searchbar/Searchbar';
import CardTable from './Components/CardTable/CardTable';

function App() {

  const [pokemon, setPokemon] = useState([]);
  const [pokemonSpecies, setPokemonSpecies] = useState([]);
  const [pokemonId, setPokemonId] = useState([]);

  const [pokemonToShow, setPokemonToShow] = useState([]);

    //'water', 'bug', 'dark', 'fighting', 'ghost', 'steel', 'flying', 'electric', 'fairy'];
    //const habitat = ['grassland', 'forest', 'waters edge', 'sea', 'cave', 'mountain', 'rough-terrain', 'urban', 'rare'];
    //const color = ['red', 'blue', 'yellow', 'green', 'black', 'brown', 'purple', 'gray', 'white', 'pink'];

  const [type, setType] = useState([{name: 'poison', active: false}, {name: 'psychic', active: false}, {name: 'grass', active: false}, {name: 'ground', active: false}, {name: 'ice', active: false}, {name: 'fire', active: false}, {name: 'rock', active: false}, {name: 'dragon', active: false}, {name: 'water', active: false}, {name: 'bug', active: false}, {name: 'dark', active: false}, {name: 'fighting', active: false}, {name: 'ghost', active: false}, {name: 'steel', active: false}, {name: 'flying', active: false}, {name: 'electric', active: false}, {name: 'fairy', active: false}])


  const toggleType = (btn) => {
    const copy = [...type];
    const found = copy.findIndex(element => element.name === btn.id);
    copy[found].active = !copy[found].active;
    setType(copy);
  };

  //async function that fetches all pokemon
  useEffect(() => {
    for(let i = 1; i < 5; i++) {
      fetch(`https://pokeapi.co/api/v2/pokemon/${i}/`
      ).then((response) => response.json()
      ).then((jsonResponse) => setPokemon((prev) => [...prev, jsonResponse])
      ).catch((error) => console.log(error));
    }
  }, []);


  //async function that fetches all pokemon species
  useEffect(() => {
    for(let i = 1; i < 5; i++) {
      fetch(`https://pokeapi.co/api/v2/pokemon-species/${i}/`
      ).then((response) => response.json()
      ).then((jsonResponse) => setPokemonSpecies((prev) => [...prev, jsonResponse])
      ).catch((error) => console.log(error));
    }
  }, []);


  //checK If pokemon should be shown
  useEffect(() => {
    let arrayOfPokemonToShow = [];

    for(let i = 0; i < pokemonId.length; i++) {
      for(let j = 0; j < pokemon.length; j++) {
        if(pokemonId[i] === pokemon[j].id) {
          arrayOfPokemonToShow.push(pokemon[j]);
        }
      }
    }

    setPokemonToShow(arrayOfPokemonToShow);

  }, [pokemonId]);



  //filters types
  useEffect(() => {

    //these are the words that we use as filters
    let filters = [];

    //the pokemon that we filtered (containers duplicates)
    let filteredPokemon = [];

    const filter = type.filter(element => element.active === true);
    filter.forEach(element => filters.push(element.name));
      pokemon.forEach(element => element.types.forEach(type => {
        for(let i = 0; i < filters.length; i++) {
          if(type.type.name === filters[i]) {
            filteredPokemon.push(element.id);
          }
        }
      }));

      //filtered pokemon without duplicates
      let unique = [...new Set(filteredPokemon)];
      setPokemonId(unique);

  }, [type])




  return (
    <div  className="App">
      <Searchbar toggleType={toggleType} type={type}/>
      <CardTable filteredPokemon={pokemonToShow}/>
    </div>
  );
}

export default App;
