import './Checkbox.css'

export default function Checkbox(props) {

    //this changes the toggle, which is responsible for the button's color
    const changeToggle = () => {
        setToggle(prev => !prev);
    }

    return (
        <div>
            <div onClick={changeToggle} className={toggle ? "Checkbox active" : "Checkbox unactive"}>
                <input id={props.value} name={props.value} type="checkbox"/>
                <label onClick={changeToggle} htmlFor={props.value}>{props.value}</label>
            </div>
        </div>
    )
}
