import React from 'react'
//css
import './Checkbox.css'

export default function Checkbox(props) {

    const [toggle, setToggle] = useState(false);

    const changeToggle = () => {
        setToggle(prev => !prev);
    }

    const handleClick = () => {
        props.changeToggle();
    }

    return (
        <div>
            <div onClick={handleClick} className={props.toggle ? "Checkbox active" : "Checkbox unactive"}>
                <input id={props.value} name={props.value} type="checkbox"/>
                <label htmlFor={props.value}>{props.value}</label>
            </div>
        </div>
    )
}
