import React, {useEffect, useState} from 'react';
//css
import './App.css'
//components
import Searchbar from './Components/Searchbar/Searchbar';
import CardTable from './Components/CardTable/CardTable';

function App() {

  const [pokemon, setPokemon] = useState([]);
  const [toggle, setToggle] = useState(false);


  const [pokemonToShow, setPokemonToShow] = useState([]);

  const type = [{placeholder: 'normal', active: false}];


  const addPokemon = async () => {
    const arrayOfPokemon = pokemon;

  }

  //this changes the toggle, which is responsible for the button's color
  const changeToggle = () => {
    if(type.active) {
      setToggle(!toggle)
    }
  }

  //async function that fetches all pokemon
  useEffect(() => {
    for(let i = 1; i < 152; i++) {
      fetch(`https://pokeapi.co/api/v2/pokemon/${i}/`
      ).then((response) => response.json()
      ).then((jsonResponse) => setPokemon((prev) => [...prev, jsonResponse])
      ).catch((error) => console.log(error));
    }
  }, []);


  return (
    <div  className="App">
      <Searchbar type={type}/>
      <CardTable info={pokemon}/>
    </div>
  );
}

export default App;
