import React, {useState} from 'react';
//css
import './App.css'
//components
import Searchbar from './Components/Searchbar/Searchbar';
import CardTable from './Components/CardTable/CardTable';

function App() {

  const [info, setInfo] = useState([]);

  const fetchPokemon = async () => {
    const pokemon = fetch('https://pokeapi.co/api/v2/pokemon/1/'
    ).then(response => response.json()
    ).then(jsonResponse => setInfo((prev) => [...prev, jsonResponse])
    ).then(log => console.log(log));
    return pokemon;
  }


  return (
    <div onLoad={fetchPokemon} className="App">
      <Searchbar/>

      <CardTable info={info}/>
    </div>
  );
}

export default App;
