import React from 'react';
//css
import './App.css'
//components
import Searchbar from './Components/Searchbar/Searchbar';
import CardTable from './Components/CardTable/CardTable';

function App() {

  const fetchPokemon = async (index) => {
    fetch(`https://pokeapi.co/api/v2/pokemon/${index}/`
    ).then(response => response.json())
  }

  const getInfo = async () => {
    const info = [];
    let pokemonJSON = await fetchPokemon(1);
    const img = await pokemonJSON.name;
    info.push(img);
    console.log(info);
  }


  return (
    <div onLoad={fetchPokemon}>
      <Searchbar onClick={getInfo}/>
      <CardTable />
    </div>
  );
}

export default App;
